import React, {useState} from 'react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const PoojaEntry = ({ poojas }) => {

    const [total, setTotal] = useState(0);

    const [data, setData] = useState([{
        "pooja": null,
        "date": null,
        "qty": null
    }]);

    const handleCreatePooja = () => {
        const newData = [...data, {
            "pooja": null,
            "date": null,
            "qty": null
        }];
       setData(newData)
    };

    const handleChangePooja = (x, selection) => {
        console.log('heelo');
        setTotal(total + poojas[x].price);
    };

    const renderPoojaOptions = (p,x) => (
        <div className="form-group">
            <label>Select Pooja</label>
            <select onChange={(e) => handleChangePooja(x, e.target.value)} value={p ? p : 'default'} className="form-control">
                <option disabled value="default">Select Pooja</option>
                { poojas.map(i =>
                    <option value={i.id}>
                        {i.name}
                    </option>
                )}
            </select>
        </div>
    );

    const renderPoojaEntry = (p,i) => (
        <div className="card p-2 my-2">
            <div className="row m-0">
                <div className="col-6">
                    <div className="form-group">
                        <label>Name</label>
                        <input className="form-control" placeholder="Enter Name" />
                    </div>
                </div>
                <div className="col-6">
                    <div className="form-group">
                        <label>Nakshatram</label>
                        <input className="form-control" placeholder="Enter Name" />
                    </div>
                </div>
                <div className="col-md-6">
                    {renderPoojaOptions(p.pooja, i)}
                </div>
                <div className="col-md-2">
                    <div className="form-group">
                        <label>Qty</label>
                        <input value={i.qty} className="form-control" type="number"/>
                    </div>
                </div>
                <div className="col-md-4">
                    <div className="form-group">
                        <label>Booking Date</label>
                        <div>
                            <DatePicker/>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );

    return (
        <div className="my-4 w-100">
            {data.map((p,i) => renderPoojaEntry(p,i))}
            <div
                onClick={handleCreatePooja}
                className="btn btn-primary"
            >
                Add Pooja
            </div>
            <div className="my-4">
                <h3>Total: {total}</h3>
            </div>
        </div>
    )

};

export default PoojaEntry;
