import React, {useState} from 'react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const PoojaEntry = ({ poojas }) => {

    const [data, setData] = useState([]);

    const handleCreatePooja = () => {
        const newData = [...data, {
            "pooja": null,
            "date": null,
            "qty": null
        }];
       setData(newData)
    };

    const renderPoojaOptions = (i) => (
        <div className="form-group">
            <label>Select Pooja</label>
            <select value={i} className="form-control">
                { poojas.map(i => <option value={i.id}>{i.name}</option>) }
            </select>
        </div>
    );

    const renderPoojaEntry = (i) => (
        <div className="card p-4">
            <div className="row m-0">
                <div className="col-md-4">
                    {renderPoojaOptions(i.pooja)}
                </div>
                <div className="col-md-2">
                    <div className="form-group">
                        <label>Qty</label>
                        <input value={i.qty} className="form-control" type="number"/>
                    </div>
                </div>
            </div>
        </div>
    );

    return (
        <div className="my-4 w-100">
            {data.map(i => renderPoojaEntry(i))}
            <div
                onClick={handleCreatePooja}
                className="btn btn-primary"
            >
                Add Pooja
            </div>
        </div>
    )

};

export default PoojaEntry;
