import React, { useState, useEffect } from 'react';
import dataFetch from "../utils/dataFetch";
import Base from "../components/Base";

const Poojas = () => {

    const [isLoaded, setLoaded] = useState(false);
    const [data, setData] = useState(false);
    const [isQueried, setQueried] = useState(false);

    const query = `{
    listPoojas
      {
        id
        name
        price
      }
    }`;

    const getPoojas = async () => await dataFetch({ query });


    useEffect(() => {
        if(!isQueried)
        {
            getPoojas().then(response => {
                setQueried(true);
                if (!Object.prototype.hasOwnProperty.call(response, 'errors')) {
                    setData(response.data.listPoojas);
                    setLoaded(true);
                }
            })
        }
    });

    const renderPoojaOptions = () => (
        <div className="form-group">
            <label>Select Pooja</label>
            <select className="form-control">
                {
                    data.map(i =>
                        <option value={i.id}>{i.name}</option>
                    )
                }
            </select>
        </div>
    )

    return (
        <Base>
            <div className="container py-4">
                <div className="row m-0">
                    <div className="card w-100 p-4">
                        <h1 className="mb-2">Create Order</h1>
                        <div className="form-group">
                            <label>Name</label>
                            <input className="form-control" placeholder="Enter Name" />
                        </div>
                        { isLoaded ? renderPoojaOptions() : null }
                    </div>
                </div>
            </div>
        </Base>
    )

};

export default Poojas;
