import React, {useState} from 'react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const PoojaEntry = ({ poojas }) => {

    const [total, setTotal] = useState(0);

    const [data, setData] = useState([{
        "pooja": null,
        "date": null,
        "qty": null
    }]);

    const handleCreatePooja = () => {
        const newData = [...data, {
            "pooja": null,
            "date": null,
            "qty": null
        }];
       setData(newData)
    };

    const handleChangePooja = (pooja, selection) => {
        console.log('heelo')
        setTotal(total + pooja.price);
    };

    const renderPoojaOptions = (i) => (
        <div className="form-group">
            <label>Select Pooja</label>
            <select value={i ? i : 'default'} className="form-control">
                <option disabled value="default">Select Pooja</option>
                { poojas.map(i =>
                    <option
                        value={i.id}
                        onChange={(e) => handleChangePooja(i, e.target.value)}
                    >
                        {i.name}
                    </option>
                )}
            </select>
        </div>
    );

    const renderPoojaEntry = (i) => (
        <div className="card p-2 my-2">
            <div className="row m-0">
                <div className="col-md-4">
                    {renderPoojaOptions(i.pooja)}
                </div>
                <div className="col-md-2">
                    <div className="form-group">
                        <label>Qty</label>
                        <input value={i.qty} className="form-control" type="number"/>
                    </div>
                </div>
                <div className="col-md-4">
                    <div className="form-group">
                        <label>Booking Date</label>
                        <div>
                            <DatePicker/>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );

    return (
        <div className="my-4 w-100">
            {data.map(i => renderPoojaEntry(i))}
            <div
                onClick={handleCreatePooja}
                className="btn btn-primary"
            >
                Add Pooja
            </div>
            <div className="my-4">
                <h3>Total: {total}</h3>
            </div>
        </div>
    )

};

export default PoojaEntry;
